    public function getSourceContext()
    {
        return new Twig_Source("{# Editando el form para que sea más bonito #}
{%- block form_row -%}
    <div class= \"control-group form-group\">
        <div class=\"controls\">
        {{- form_label(form) -}}
        {{- form_errors(form) -}}
        {{- form_widget(form) -}}
        </div>
    </div>
{%- endblock form_row -%}

{%- block form_widget_simple -%}
    {%- set type = type|default('text') -%}
    <input type=\"{{ type }}\" class= \"form-control\" {{ block('widget_attributes') }} {% if value is not empty %}value=\"{{ value }}\" {% endif %}/>

    {%- if type == 'file' -%}
    <img id= \"corteThumb\" class=\"img-fluid\" width=\"150px\" src=\"\" alt=\"\">
    {%- endif -%}

{%- endblock form_widget_simple -%}

{%- block textarea_widget -%}
    <textarea class= \"form-control\" {{ block('widget_attributes') }}>{{ value }}</textarea>
{%- endblock textarea_widget -%}


{%- block button_widget -%}
    {%- if label is empty -%}
        {%- if label_format is not empty -%}
            {% set label = label_format|replace({
                '%name%': name,
                '%id%': id,
            }) %}
        {%- elseif label is same as(false) -%}
            {% set translation_domain = false %}
        {%- else -%}
            {% set label = name|humanize %}
        {%- endif -%}
    {%- endif -%}
    <button type=\"{{ type|default('button') }}\" class=\"btn btn-primary\"{{ block('button_attributes') }}>{{ translation_domain is same as(false) ? label : label|trans({}, translation_domain) }}</button>
{%- endblock button_widget -%}", "form/corteForm.html.twig", "/home/tomy/webapi/app/Resources/views/form/corteForm.html.twig");
    }
